// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.0;

import {Errors} from "../utils/Errors.sol";

/**
 * @title PercentageMath library
 * @author Aave
 * @notice Provides functions to perform percentage calculations
 * @dev Percentages are defined by default with 2 decimals of precision (100.00). The precision is indicated by PERCENTAGE_FACTOR
 * @dev Operations are rounded half up
 **/

library PercentageMath {
    uint256 constant PERCENTAGE_FACTOR = 1e4; //percentage plus two decimals
    uint256 constant HALF_PERCENT = PERCENTAGE_FACTOR / 2;

    /**
     * @dev Executes a percentage multiplication
     * @param value The value of which the percentage needs to be calculated
     * @param percentage The percentage of the value to be calculated
     * @return The percentage of value
     **/
    function percentMul(uint256 value, uint256 percentage)
        internal
        pure
        returns (uint256)
    {
        if (value == 0 || percentage == 0) {
            return 0;
        }

        require(
            value <= (type(uint256).max - HALF_PERCENT) / percentage,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (value * percentage + HALF_PERCENT) / PERCENTAGE_FACTOR;
    }

    /**
     * @dev Executes a percentage division
     * @param value The value of which the percentage needs to be calculated
     * @param percentage The percentage of the value to be calculated
     * @return The value divided the percentage
     **/
    function percentDiv(uint256 value, uint256 percentage)
        internal
        pure
        returns (uint256)
    {
        require(percentage != 0, Errors.MATH_DIVISION_BY_ZERO);
        uint256 halfPercentage = percentage / 2;

        require(
            value <= (type(uint256).max - halfPercentage) / PERCENTAGE_FACTOR,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (value * PERCENTAGE_FACTOR + halfPercentage) / percentage;
    }
}

contracts/math/WadRayMath.sol.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.0;

import {Errors} from "../utils/Errors.sol";

/**
 * @title WadRayMath library
 * @author Aave
 * @dev Provides mul and div function for wads (decimal numbers with 18 digits precision) and rays (decimals with 27 digits)
 **/

library WadRayMath {
    uint256 internal constant WAD = 1e18;
    uint256 internal constant halfWAD = WAD / 2;

    uint256 internal constant RAY = 1e27;
    uint256 internal constant halfRAY = RAY / 2;

    uint256 internal constant WAD_RAY_RATIO = 1e9;

    /**
     * @return One ray, 1e9
     **/
    function ray() internal pure returns (uint256) {
        return RAY;
    }

    /**
     * @return One wad, 1e6
     **/

    function wad() internal pure returns (uint256) {
        return WAD;
    }

    /**
     * @return Half ray, 1e9/2
     **/
    function halfRay() internal pure returns (uint256) {
        return halfRAY;
    }

    /**
     * @return Half ray, 1e6/2
     **/
    function halfWad() internal pure returns (uint256) {
        return halfWAD;
    }

    function toRay(uint256 num) external pure returns (uint256) {
        return (num * 1e25);
    }

    /**
     * @dev Multiplies two wad, rounding half up to the nearest wad
     * @param a Wad
     * @param b Wad
     * @return The result of a*b, in wad
     **/
    function wadMul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0 || b == 0) {
            return 0;
        }

        require(
            a <= (type(uint256).max - halfWAD) / b,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (a * b + halfWAD) / WAD;
    }

    /**
     * @dev Divides two wad, rounding half up to the nearest wad
     * @param a Wad
     * @param b Wad
     * @return The result of a/b, in wad
     **/
    function wadDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b != 0, Errors.MATH_DIVISION_BY_ZERO);
        uint256 halfB = b / 2;

        require(
            a <= (type(uint256).max - halfB) / WAD,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (a * WAD + halfB) / b;
    }

    /**
     * @dev Multiplies two ray, rounding half up to the nearest ray
     * @param a Ray
     * @param b Ray
     * @return The result of a*b, in ray
     **/
    function rayMul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0 || b == 0) {
            return 0;
        }

        require(
            a <= (type(uint256).max - halfRAY) / b,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (a * b + halfRAY) / RAY;
    }

    /**
     * @dev Divides two ray, rounding half up to the nearest ray
     * @param a Ray
     * @param b Ray
     * @return The result of a/b, in ray
     **/
    function rayDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b != 0, Errors.MATH_DIVISION_BY_ZERO);
        uint256 halfB = b / 2;

        require(
            a <= (type(uint256).max - halfB) / RAY,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );

        return (a * RAY + halfB) / b;
    }

    /**
     * @dev Casts ray down to wad
     * @param a Ray
     * @return a casted to wad, rounded half up to the nearest wad
     **/
    function rayToWad(uint256 a) internal pure returns (uint256) {
        uint256 halfRatio = WAD_RAY_RATIO / 2;
        uint256 result = halfRatio + a;
        require(result >= halfRatio, Errors.MATH_ADDITION_OVERFLOW);

        return result / WAD_RAY_RATIO;
    }

    /**
     * @dev Converts wad up to ray
     * @param a Wad
     * @return a converted in ray
     **/
    function wadToRay(uint256 a) internal pure returns (uint256) {
        uint256 result = a * WAD_RAY_RATIO;
        require(
            result / WAD_RAY_RATIO == a,
            Errors.MATH_MULTIPLICATION_OVERFLOW
        );
        return result;
    }
}

contracts/ToTheMooon.sol.content:
// SPDX-License-Identifier: Apache 2.0
pragma solidity ^0.8.0;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import {IToTheMooon} from "./interfaces/IToTheMooon.sol";
import {ITournament} from "./interfaces/ITournament.sol";
import {Tournament} from "./Tournament.sol";
import {Errors} from "./utils/Errors.sol";
import {DataTypes} from "./utils/DataTypes.sol";

/**
 * @title ToTheMooon contract
 * @author Sumit Mahajan
 * @dev Primary contract for the ToTheMoon game
 **/
contract ToTheMooon is IToTheMooon, Ownable {
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.Bytes32Set;

    using Strings for string;

    mapping(address => DataTypes.PlayerStruct) players;

    EnumerableSet.Bytes32Set usernames;

    mapping(uint256 => DataTypes.TournamentStruct) public tournaments;

    mapping(uint256 => uint256) public override r_values;

    uint256 public currentTournamentId;

    uint256 public override timeLimit = 86400;

    uint256 public override joiningFees = 0.0001 ether;

    uint256 public override winnersPercentage = 6900;

    constructor() {
        r_values[10] = 1.15e18;
        r_values[20] = 1.075e18;
        r_values[30] = 1.05e18;
        r_values[40] = 1.025e18;
        r_values[50] = 1.02e18;
        r_values[100] = 1.01e18;
        r_values[1000] = 1.005e18;
    }

    function getOwner() public view override returns (address) {
        return owner();
    }

    function getJoinedTournamentsByPlayer(
        address _player
    ) external view override returns (DataTypes.TournamentStruct[] memory) {
        DataTypes.TournamentStruct[]
            memory pTournaments = new DataTypes.TournamentStruct[](
                currentTournamentId
            );

        for (uint256 i = 0; i < players[_player].tournamentIds.length(); i++) {
            pTournaments[i] = tournaments[players[_player].tournamentIds.at(i)];
        }

        return pTournaments;
    }

    function hasJoinedTournament(
        address _wallet,
        uint256 tournamentId
    ) external view override returns (bool) {
        return players[_wallet].tournamentIds.contains(tournamentId);
    }

    function getUserName(
        address wallet_
    ) external view override returns (string memory) {
        DataTypes.PlayerStruct storage player = players[wallet_];
        return player.username;
    }

    function setUserName(
        string memory _name,
        uint256 _strlen
    ) external override {
        require(
            !usernames.contains(keccak256(abi.encodePacked(_name))),
            Errors.USERNAME_TAKEN
        );

        DataTypes.PlayerStruct storage player = players[msg.sender];

        usernames.remove(keccak256(abi.encodePacked(player.username)));
        usernames.add(keccak256(abi.encodePacked(_name)));

        player.username = _strlen > 20 ? substring(_name, 0, 20) : _name;
    }

    function addPlayerToTournament(address _playerAddress) external override {
        require(
            msg.sender == tournaments[currentTournamentId].contractAddress,
            Errors.CALLER_NOT_AUTHORIZED
        );

        DataTypes.PlayerStruct storage player = players[_playerAddress];

        player.tournamentIds.add(currentTournamentId);

        if (!player.hasRegistered) {
            player.hasRegistered = true;
        }
    }

    function createTournament(
        string memory _name,
        DataTypes.RewardVars memory _rewardVars
    ) external payable override onlyOwner {
        require(
            currentTournamentId == 0 ||
                (tournaments[currentTournamentId].startTime + timeLimit <
                    block.timestamp &&
                    ITournament(
                        tournaments[currentTournamentId].contractAddress
                    ).hasEnded()),
            Errors.TOURNAMENT_IS_LIVE
        );
        require(
            _rewardVars.isSponsored ? msg.value == _rewardVars.prizePool : true,
            Errors.NOT_ENOUGH_FUNDS_PROVIDED
        );

        ITournament tournament = new Tournament(
            ++currentTournamentId,
            _name,
            block.timestamp,
            address(this),
            _rewardVars
        );
        tournaments[currentTournamentId] = DataTypes.TournamentStruct(
            currentTournamentId,
            _name,
            block.timestamp,
            address(tournament),
            _rewardVars
        );
    }

    function setTimeLimit(uint256 timeLimit_) external override onlyOwner {
        timeLimit = timeLimit_;
    }

    function setJoiningFees(uint256 joiningFees_) external override onlyOwner {
        joiningFees = joiningFees_;
    }

    function substring(
        string memory str,
        uint256 startIndex,
        uint256 endIndex
    ) internal pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        bytes memory result = new bytes(endIndex - startIndex);
        for (uint256 i = startIndex; i < endIndex; i++) {
            result[i - startIndex] = strBytes[i];
        }
        return string(result);
    }
}

contracts/Tournament.sol.content:
// SPDX-License-Identifier: Apache 2.0
pragma solidity ^0.8.0;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import {IToTheMooon} from "./interfaces/IToTheMooon.sol";
import {ITournament} from "./interfaces/ITournament.sol";
import {PercentageMath} from "./math/PercentageMath.sol";
import {WadRayMath} from "./math/WadRayMath.sol";
import {Errors} from "./utils/Errors.sol";
import {DataTypes} from "./utils/DataTypes.sol";

/**
 * @title Tournament contract
 * @author Sumit Mahajan
 * @dev Handles Tournament logic for sponsored and daily tournaments of ToTheMooon
 **/
contract Tournament is ITournament, ReentrancyGuard {
    using EnumerableSet for EnumerableSet.AddressSet;

    using PercentageMath for uint256;
    using WadRayMath for uint256;

    uint256 public id;

    string public name;

    uint256 public startTime;

    uint256 public timeLimit;

    uint256 public joiningFees;

    address public gameContractAddress;

    address public approvedCaller;

    bool public override hasEnded;

    DataTypes.RewardVars public rewardVars;

    EnumerableSet.AddressSet playerListSet;

    mapping(address => DataTypes.PlayerStats) public playerStatsMap;

    event ScoreRecorded(
        address indexed player,
        uint256 indexed timestamp,
        uint256 score
    );

    event FundsAdded(
        address indexed payer,
        uint256 indexed timestamp,
        uint256 funds
    );

    event FundsWithdrawn(uint256 indexed timestamp, uint256 funds);

    constructor(
        uint256 id_,
        string memory name_,
        uint256 startTime_,
        address gameContractAddress_,
        DataTypes.RewardVars memory _rewardVars
    ) {
        id = id_;
        name = name_;
        startTime = startTime_;
        gameContractAddress = gameContractAddress_;
        timeLimit = IToTheMooon(gameContractAddress).timeLimit();
        joiningFees = IToTheMooon(gameContractAddress).joiningFees();
        approvedCaller = IToTheMooon(gameContractAddress).getOwner();
        rewardVars = _rewardVars;
    }

    modifier onlyApprovedCaller() {
        require(msg.sender == approvedCaller, Errors.CALLER_NOT_AUTHORIZED);
        _;
    }

    receive() external payable {
        emit FundsAdded(msg.sender, block.timestamp, msg.value);
    }

    function withdrawContractFunds(
        uint256 funds
    ) external onlyApprovedCaller nonReentrant {
        require(funds <= address(this).balance, Errors.NOT_ENOUGH_FUNDS);
        (bool success, ) = msg.sender.call{value: funds}("");
        require(success, "Transfer failed.");
        // payable(msg.sender).transfer(funds);

        emit FundsWithdrawn(block.timestamp, funds);
    }

    function getTournamentInfo()
        public
        view
        override
        returns (
            string memory _name,
            uint256 _timeLimit,
            uint256 _startTime,
            uint256 _id,
            uint256 _joiningFees,
            DataTypes.RewardVars memory _rewardVars,
            uint256 _nPlayers
        )
    {
        (_rewardVars, , _nPlayers) = getRewardInfo();

        _name = name;
        _timeLimit = timeLimit;
        _startTime = startTime;
        _id = id;
        _joiningFees = joiningFees;
    }

    function getRewardInfo()
        public
        view
        override
        returns (DataTypes.RewardVars memory, uint256, uint256)
    {
        return (
            DataTypes.RewardVars(
                rewardVars.isSponsored,
                rewardVars.isSponsored
                    ? rewardVars.prizePool
                    : playerListSet.length() *
                        joiningFees -
                        (playerListSet.length() * joiningFees).percentMul(
                            rewardVars.commissionPercentage
                        ),
                min(
                    playerListSet.length().percentMul(
                        IToTheMooon(gameContractAddress).winnersPercentage()
                    ),
                    rewardVars.isSponsored
                        ? rewardVars.nWinners
                        : IToTheMooon(gameContractAddress).winnersPercentage() /
                            10
                ),
                rewardVars.sponsorMetadata,
                rewardVars.commissionPercentage
            ),
            joiningFees,
            playerListSet.length()
        );
    }

    function getLeaderBoard()
        public
        view
        override
        returns (DataTypes.LeaderBoardEntry[] memory)
    {
        DataTypes.LeaderBoardEntry[]
            memory leaderboardTemp = new DataTypes.LeaderBoardEntry[](
                playerListSet.length()
            );

        if (playerListSet.length() == 0) {
            return leaderboardTemp;
        }

        for (uint256 i = 0; i < playerListSet.length(); i++) {
            leaderboardTemp[i] = DataTypes.LeaderBoardEntry(
                playerListSet.at(i),
                IToTheMooon(gameContractAddress).getUserName(
                    playerListSet.at(i)
                ),
                playerStatsMap[playerListSet.at(i)].highscore,
                0
            );
        }

        leaderboardTemp = quickSort(
            leaderboardTemp,
            0,
            int256(playerListSet.length() - 1)
        );

        (DataTypes.RewardVars memory currentRewardVars, , ) = getRewardInfo();

        uint256 r = getRValue(playerListSet.length());

        uint256 a = (currentRewardVars.prizePool.wadMul(r - 1e18)).wadDiv(
            pow(r, currentRewardVars.nWinners) - 1e18
        );

        for (uint256 i = 0; i < currentRewardVars.nWinners; i++) {
            leaderboardTemp[i].prize = a.wadMul(
                pow(r, (currentRewardVars.nWinners - i - 1))
            );
        }

        return leaderboardTemp;
    }

    function joinTournament() external payable override {
        require(
            startTime + timeLimit > block.timestamp,
            Errors.TOURNAMENT_HAS_ENDED
        );

        require(msg.value == joiningFees, Errors.NOT_ENOUGH_FUNDS_PROVIDED);

        playerListSet.add(msg.sender);
        playerStatsMap[msg.sender].attemptsLeft = 3;
        IToTheMooon(gameContractAddress).addPlayerToTournament(msg.sender);
    }

    function reFill() external payable override {
        require(
            startTime + timeLimit > block.timestamp,
            Errors.TOURNAMENT_HAS_ENDED
        );

        bool hasPlayerJoined = IToTheMooon(gameContractAddress)
            .hasJoinedTournament(msg.sender, id);
        require(hasPlayerJoined, Errors.PLAYER_NOT_JOINED);

        require(msg.value == joiningFees, Errors.NOT_ENOUGH_FUNDS_PROVIDED);

        require(
            playerStatsMap[msg.sender].attemptsLeft == 0,
            Errors.ATTEMPTS_LEFT
        );

        playerStatsMap[msg.sender].attemptsLeft = 3;
    }

    function recordScore(
        address _player,
        uint256 _score
    ) external override onlyApprovedCaller {
        require(
            startTime + timeLimit > block.timestamp,
            Errors.TOURNAMENT_HAS_ENDED
        );

        bool hasPlayerJoined = IToTheMooon(gameContractAddress)
            .hasJoinedTournament(_player, id);
        require(hasPlayerJoined, Errors.PLAYER_NOT_JOINED);

        require(
            playerStatsMap[_player].attemptsLeft > 0,
            Errors.NO_ATTEMPTS_LEFT
        );

        playerStatsMap[_player].highscore = _score;
        playerStatsMap[_player].attemptsLeft--;

        emit ScoreRecorded(_player, block.timestamp, _score);
    }

    function endTournament() external override onlyApprovedCaller {
        require(
            startTime + timeLimit < block.timestamp,
            Errors.TOURNAMENT_IS_LIVE
        );

        require(!hasEnded, Errors.TOURNAMENT_HAS_ENDED);

        hasEnded = true;

        DataTypes.LeaderBoardEntry[] memory leaderboardTemp = getLeaderBoard();

        for (uint256 i = 0; i < playerListSet.length(); i++) {
            playerStatsMap[leaderboardTemp[i].wallet] = DataTypes.PlayerStats(
                i + 1,
                leaderboardTemp[i].highscore,
                playerStatsMap[leaderboardTemp[i].wallet].attemptsLeft,
                playerStatsMap[leaderboardTemp[i].wallet].reFills,
                leaderboardTemp[i].prize,
                leaderboardTemp[i].prize
            );
        }
    }

    function withdrawPrize() external override nonReentrant {
        require(
            startTime + timeLimit < block.timestamp,
            Errors.TOURNAMENT_IS_LIVE
        );

        bool hasPlayerJoined = IToTheMooon(gameContractAddress)
            .hasJoinedTournament(msg.sender, id);

        require(hasPlayerJoined, Errors.PLAYER_NOT_JOINED);

        DataTypes.PlayerStats storage player = playerStatsMap[msg.sender];

        if (player.unclaimedPrize > 0) {
            uint256 temp = player.unclaimedPrize;
            player.unclaimedPrize = 0;
            (bool success, ) = msg.sender.call{value: temp}("");
            require(success, "Transfer failed.");
            // payable(msg.sender).transfer(temp);
        }
    }

    function getRValue(uint256 nPlayers) internal view returns (uint256) {
        if (nPlayers < 10) {
            return IToTheMooon(gameContractAddress).r_values(10);
        } else if (nPlayers < 20) {
            return IToTheMooon(gameContractAddress).r_values(20);
        } else if (nPlayers < 30) {
            return IToTheMooon(gameContractAddress).r_values(30);
        } else if (nPlayers < 40) {
            return IToTheMooon(gameContractAddress).r_values(40);
        } else if (nPlayers < 50) {
            return IToTheMooon(gameContractAddress).r_values(50);
        } else if (nPlayers < 100) {
            return IToTheMooon(gameContractAddress).r_values(100);
        } else {
            return IToTheMooon(gameContractAddress).r_values(1000);
        }
    }

    function quickSort(
        DataTypes.LeaderBoardEntry[] memory leaderboardTemp,
        int256 left,
        int256 right
    ) internal view returns (DataTypes.LeaderBoardEntry[] memory) {
        if (left >= right) return leaderboardTemp;
        uint256 pivot = leaderboardTemp[uint256(left + (right - left) / 2)]
            .highscore;
        (int256 i, int256 j) = (left, right);
        while (i <= j) {
            while (leaderboardTemp[uint256(i)].highscore > pivot) i++;
            while (pivot > leaderboardTemp[uint256(j)].highscore) j--;
            if (i <= j) {
                DataTypes.LeaderBoardEntry memory tmp = leaderboardTemp[
                    uint256(i)
                ];
                leaderboardTemp[uint256(i)] = leaderboardTemp[uint256(j)];
                leaderboardTemp[uint256(j)] = tmp;
                i++;
                j--;
            }
        }
        if (left < j) quickSort(leaderboardTemp, left, j);
        if (i < right) quickSort(leaderboardTemp, i, right);

        return leaderboardTemp;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a > b) {
            return b;
        }
        return a;
    }

    function pow(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 ans = a;

        if (b == 0) return 1e18;

        for (uint256 i = 1; i < b; i++) {
            ans = ans.wadMul(a);
        }
        return ans;
    }
}

contracts/utils/DataTypes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

library DataTypes {
    struct PlayerStruct {
        bool hasRegistered;
        string username;
        EnumerableSet.UintSet tournamentIds;
    }

    struct TournamentStruct {
        uint id;
        string name;
        uint startTime;
        address contractAddress;
        RewardVars rewardVars;
    }

    struct PlayerStats {
        uint rank;
        uint highscore;
        uint attemptsLeft;
        uint reFills;
        uint allocatedPrize;
        uint unclaimedPrize;
    }

    struct LeaderBoardEntry {
        address wallet;
        string username;
        uint highscore;
        uint prize;
    }

    struct RewardVars {
        bool isSponsored;
        uint prizePool;
        uint nWinners;
        string sponsorMetadata;
        uint commissionPercentage;
    }
}

contracts/utils/Errors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library Errors {
    string public constant CALLER_NOT_AUTHORIZED = "CALLER_NOT_AUTHORIZED";
    string public constant USERNAME_TAKEN = "USERNAME_TAKEN";
    string public constant TOURNAMENT_IS_LIVE = "TOURNAMENT_IS_LIVE";
    string public constant TOURNAMENT_HAS_ENDED = "TOURNAMENT_HAS_ENDED";
    string public constant PLAYER_NOT_JOINED = "PLAYER_NOT_JOINED";
    string public constant NOT_ENOUGH_FUNDS_PROVIDED =
        "NOT_ENOUGH_FUNDS_PROVIDED";
    string public constant NOT_ENOUGH_FUNDS = "NOT_ENOUGH_FUNDS";
    string public constant NO_ATTEMPTS_LEFT = "NO_ATTEMPTS_LEFT";
    string public constant ATTEMPTS_LEFT = "ATTEMPTS_LEFT";

    string public constant MATH_ADDITION_OVERFLOW = "MATH_ADDITION_OVERFLOW";
    string public constant MATH_MULTIPLICATION_OVERFLOW =
        "MATH_MULTIPLICATION_OVERFLOW";
    string public constant MATH_DIVISION_BY_ZERO = "MATH_DIVISION_BY_ZERO";
}

